	///////////////////////////////////////////
	// Site Logo
	///////////////////////////////////////////
	function themify_site_logo($data=array()){
		if($data['attr']['target'] != ''){
			$target = "<span class='hide target'>".$data['attr']['target']."</span>";	
		}
		$data = get_data();
		if($data['setting-site_logo'] == "image"){
			$image = "checked='checked'";
			$image_display = "style='display:block;'";
			$text_display = "style='display:none;'";
		} else {
			$text = "checked='checked'";	
			$text_display = "style='display:block;'";
			$image_display = "style='display:none;'";
		}
		return '<p>
					<span class="label">Display</span> 
						<input name="setting-site_logo" type="radio" value="text" '.$text.' /> Site Title 
						<input name="setting-site_logo" type="radio" value="image" '.$image.' /> Image
					</span>
					'.$target.'
					<span class="pushlabel image" '.$image_display.'>
						<input type="text" class="width10" name="setting-site_logo_image_value" value="'.$data['setting-site_logo_image_value'].'" />
					</span>
					<span class="pushlabel image" '.$image_display.'>
						<a href="#" id="setting-'.$data['category'].'-'.$data['title'].'-site_logo" class="upload-btn upload-image logo">+ Upload</a>
					</span>
					<span class="pushlabel clear image" '.$image_display.'>
						<input type="text" name="setting-site_logo_width" class="width2" value="'.$data['setting-site_logo_width'].'" /> width 
						<input type="text" name="setting-site_logo_height" class="width2" value="'.$data['setting-site_logo_height'].'" /> height
					</span>
				</p>';	
	}
	
