	///////////////////////////////////////////
	// Search Settings Module
	///////////////////////////////////////////
	function themify_search_settings($data=array()){
		$data = get_data();
		if($data['setting']['search_settings_exclude']){
			$checked = "checked='checked'";	
		}
		return '<p><span class="label">Search in Category IDs </span>
					   <input type="text" class="width6" name="setting-search_settings" value="'.$data['setting-search_settings'].'" />
					</p>
					<p>
						<span class="pushlabel"><small>Use minus sign (-) to exclude categories.</small></span><br />
						<span class="pushlabel"><small>Example: (1,4,-7) = search only in Category 1 &amp; 4, and exclude Category 7.</small></span>
					</p>
					<p><span class="pushlabel"><input type="checkbox" name="setting-search_settings_exclude" '.$checked.'/> Exclude Pages in search results</span></p>';	
	}
	
	///////////////////////////////////////////
	// Search Settings Module - Action 
	///////////////////////////////////////////
	function themify_search_settings_action($query){
		$data = get_data();
		if($query->is_search){
			if($data['setting-search_settings'] != ""){
				$query->set('cat',$data['setting-search_settings']);
			}
			if($data['setting-search_settings_exclude']){
				$query->set('post_type','post');
			} 
		}
		return $query;
	}
	add_filter('pre_get_posts','themify_search_settings_action');
	
